<?php
include '/etc/freepbx.conf';
if (empty($argv[1])) {
	die();
}

/*
 * params list:
 * ============
 *
 * 0: sender
 * 1: receiver
 * 2: room
 * 3: message
 *
 */

$params = explode(":", $argv[1], 4);
$sender = \FreePBX::Userman()->getUserByUsername($params[0]);
$receiver = \FreePBX::Userman()->getUserByUsername($params[1]);
$room = $params[2];
$now = date("F j h:i A" );
$message = $params[3];

if (empty($sender) || empty($receiver)) {
	die();
}

$data = \FreePBX::Contactmanager()->getImageByID($sender['id'], $sender['email'], $sender['type']);

if (!empty($data['image'])) {
	$buffer = base64_encode($data['image']);
	$senderAvatar = <<<EOF
			<td width="15%">
			<img src="data:image/png;base64,$buffer" alt="$sender[displayname]" height="56" width="55">
			</td>
EOF;
} else {
	$noimage = "data:image/png;base64,";
	$senderAvatar = "<td width=\"15%\" height=\"56\" width=\"55\"><img src=\"$noimage\" alt=\"No Image\"></td>";
}

