$mailer = new \CI_Email();
$mailer->from($sender["email"]);
$mailer->to($receiver["email"]);
$mailer->subject("$sender[displayname] just mentioned you in the room $room but you're offline");
$mailer->set_mailtype('html');
$mailer->message($htmlMessage);
$mailer->send();
